scwin.findCommonSurveyIndices = function (array1, array2) {
  const commonIndices = [];

  array1.forEach((item1, index1) => {
    const existsInArray2 = array2.some(item2 => item2.survey_id === item1.survey_id);
    if (existsInArray2) {
      commonIndices.push(index1); // survey_id 같은 객체의 arr1에서의 인덱스
    }
  });

  return commonIndices;
}

/**
 * 조회 버튼 클릭의 콜백 메소드 (후처리함수)
 * 건수 조회, ReadOnly 그리드뷰 컬럼색 변경. ReadOnly속성을 설정한다. 
 */
scwin.sbm_searchMemberBasic_submitdone = function (e) {
	if (com.sbm.getResultCode(e) == gcm.MESSAGE_CODE.STATUS_SUCCESS) {
		spn_memberCnt.setValue(surDataList.getRowCount());
		var data = e.responseJSON;
		console.log("data!!!!!: ", data)
		var participatedSurDataList = data.participatedSurDataList;

		// paging
		var totrowcnt = surDataList.getRowCount();
		scwin.setPageListCount(totrowcnt);
		dlt_surDataList_paging.setJSON(surDataList.getRangeJSON(0, scwin.gridRowCount-1));
		scwin.setNo(totrowcnt);
		// 참여여부 
		scwin.participatedIdxArr = scwin.findCommonSurveyIndices(data.surDataList, participatedSurDataList);
		console.log("RESULT: ",scwin.participatedIdxArr);
		// 공통된 인덱스들에 해당하는 행에 서식을 적용한다
/**/ 		for (var i = 0; i < scwin.participatedIdxArr.length; i++) {
			// 현재 페이지에 해당하는 인덱스만 서식 적용
			if (scwin.participatedIdxArr[i]  >= (scwin.currentPage - 1) * scwin.gridRowCount 
				&& scwin.participatedIdxArr[i]  < scwin.currentPage * scwin.gridRowCount) {
			// 실제 인덱스를 그리드의 인덱스로 변환
			var gridIndex = scwin.participatedIdxArr[i] % scwin.gridRowCount;
			console.log("Actual Row: ", scwin.participatedIdxArr[i], "Grid Index: ", gridIndex);

			// 그리드의 해당 행에 서식 적용
			grd_memberBasic.setRowBackgroundColor(gridIndex, "#E8E9E8");
			}
		} 

		// 완료여부 세팅
		// 데이터를 그리드뷰에 바인딩
        //grd_memberBasic.bind(surDataList);
        // 현재 날짜 가져오기
        var today = new Date();
        // 그리드뷰의 각 행에 대해 처리
        for (var i = 0; i < surDataList.getTotalRow(); i++) {

			if (scwin.participatedIdxArr.includes(i)) {
                // 참여여부 세팅
                surDataList.setCellData(i, "participateTF", "참여완료");
                dlt_surDataList_paging.setCellData(i, "participateTF", "참여완료");
            } else {
                // 공통된 인덱스가 아닐 경우 다른 값을 세팅
                surDataList.setCellData(i, "participateTF", "미참여");
                dlt_surDataList_paging.setCellData(i, "participateTF", "미참여");
            }

			var startDate = new Date(surDataList.getCellData(i, "start_date"));
            var endDate = new Date(surDataList.getCellData(i, "end_date"));
			var todayFormat = today.getFullYear() + '-' + 
				String(today.getMonth() + 1).padStart(2, '0') + '-' + 
				String(today.getDate()).padStart(2, '0');

            // 현재 날짜와 시작일, 종료일을 비교하여 상태 설정
			if (todayFormat >= surDataList.getCellData(i, "start_date") && todayFormat <= surDataList.getCellData(i, "end_date")) {
				surDataList.setCellData(i, "state", "진행중");
				dlt_surDataList_paging.setCellData(i, "state", "진행중");
			}
			else if (today.getTime() >= startDate.getTime() && today.getTime() <= endDate.getTime()) {
				surDataList.setCellData(i, "state", "진행중");
				dlt_surDataList_paging.setCellData(i, "state", "진행중");
			} else if(todayFormat > surDataList.getCellData(i, "end_date")) {
				surDataList.setCellData(i, "state", "완료");
				dlt_surDataList_paging.setCellData(i, "state", "완료");			
			}
			else if (today.getTime() > endDate.getTime()) {
				surDataList.setCellData(i, "state", "완료");
				dlt_surDataList_paging.setCellData(i, "state", "완료");
			} else {
				surDataList.setCellData(i, "state", "진행예정");
				dlt_surDataList_paging.setCellData(i, "state", "진행예정");
			}
		} 
		grd_memberBasic.setFocusedCell(0, "title"); // onrowindexexchange 이벤트 발생
	}
};

/**
 * 페이지리스트 컴포넌트의 전체 페이지수 세팅
 */
scwin.setPageListCount = function(totrowcnt) {
	var cnt = Number(totrowcnt) / scwin.gridRowCount;
	cnt = Math.ceil(cnt);

	pageList3.setCount(cnt);
}
/**
 * 목록 그리드뷰의 no 컬럼 값 세팅
 */
scwin.setNo = function(startNo){
	var len = scwin.gridRowCount;
	for(var ix = 0; ix < len; ix++){
		dlt_surDataList_paging.setCellData(ix, "no", startNo--);
	}
}
/**
 * 페이지리스트 컴포넌트의 숫자 누르면 실행되는 함수
 */
scwin.pageList3_onchange = function(current,before){
	console.log("pageList3_onchange, current = " + current + ", before = " + before);
	console.log("scwin.participatedIdxArr = " + scwin.participatedIdxArr);
	scwin.currentPage = current; // 현재 페이지 저장

	var start ;
	var end;
	var getJson;

	start = (current-1)*scwin.gridRowCount;
	end = start+scwin.gridRowCount-1;
	getJson = surDataList.getRangeJSON(start, end);
	dlt_surDataList_paging.setJSON(getJson);

	// 내림차순으로 no 설정
    var totalRows = surDataList.getRowCount();
    var startNo = totalRows - start;
	scwin.setNo(startNo);

	// 공통된 인덱스들(설문 참여한 행)에 해당하는 행에 서식을 적용한다
	for (var i = 0; i < scwin.participatedIdxArr.length; i++) {
		// 실제 인덱스 계산

		// 현재 페이지에 해당하는 인덱스만 서식 적용
		if (scwin.participatedIdxArr[i]  >= (scwin.currentPage - 1) * scwin.gridRowCount 
			&& scwin.participatedIdxArr[i]  < scwin.currentPage * scwin.gridRowCount) {
		// 실제 인덱스를 그리드의 인덱스로 변환
		var gridIndex = scwin.participatedIdxArr[i] % scwin.gridRowCount;
		console.log("Actual Row: ", scwin.participatedIdxArr[i], "Grid Index: ", gridIndex);

		// 그리드의 해당 행에 서식 적용
		grd_memberBasic.setRowBackgroundColor(gridIndex, "#E8E9E8");
		}
	}
};

/**
 * 설문 항목 라디오 선택 시 함수
 */
scwin.rad_option_text1_onviewchange = function(info) {
	// dlt_participateList에 응답 데이터 바인딩하기
	var idx = this.getGeneratedIndex(); // 몇번째 질문에 대한 항목인지 인덱스

	var itemIndex = info.index;         // item의 index
    var itemValue = info.value;         // item의 value : sel_option_num
	var compValue = this.getValue();    // 컴포넌트의 value
	//  idx번째 질문의 itemIndex번째 항목
	var question_id = dlt_QList.getCellData(idx, "id");
	var option_id = Object.values(scwin.quMap)[idx].children[itemIndex].id;
	var reasonComponent = generator2.getChild(idx, "ipt_choiceReason1");
	var reason = reasonComponent.getValue();
	/* var jsonData = [
					{
						"question_id": 2,
						"sel_option_num": 2,
						"option_id": 2,
						"reason":2
					}
				]; */

	// dlt_participateList에 새롭게 넣을 question_id와 같은 데이터가 있는지 확인 : 있으면 지우고 데이터 넣는다.
	var existingRows = dlt_participateList.getMatchedJSON("question_id", question_id, true, 0, dlt_participateList.getTotalRow());
	console.log(existingRows); // json list
	if (existingRows.length > 0) {
		var indexArr = dlt_participateList.getMatchedIndex("question_id", question_id, true, 0, dlt_participateList.getTotalRow());
		for (var i = 0; i < indexArr.length; i++){
			dlt_participateList.removeRow(indexArr[i]);
		}
	}
	dlt_participateList.setRowData(
									idx, 
									[question_id,itemValue,option_id,reason], 
									false
								); // false로 해야 들어감!
	// true면 기존 인덱스 존재해야 덮어씀, false면 새로운 행 생성해 넣음.

	//console.log(dlt_participateList); // 라디오 선택 후 적는 선택사유는 못가져오는 문제 있음
};

scwin.ipt_choiceReason1_onchange = function(info) { //선택사유 바인딩
	/* if(info.newValue.length >= 50) {
		alert("50자까지만 적을 수 있어요.");
	} */
	var inputValue = info.newValue;
	console.log(inputValue);

	var idx = this.getGeneratedIndex(); // 몇번째 질문에 대한 항목인지 인덱스
	var reasonComponent = generator2.getChild(idx, "ipt_choiceReason1");

	if(inputValue.trim().length < 1) {
		//alert("공백이 아닌 내용을 적어주세요.");
		reasonComponent.setValue("");
	} 

	var reason = reasonComponent.getValue();
	dlt_participateList.setCellData(idx, "reason", reason.trim()); // 양옆 공백 제거후 저장
};

/**
 * 설문 응답 후 제출 버튼 클릭한다.
 */
scwin.btn_regist_onclick = function () {
	if(surDataMap.get("state") !== "진행중") { // 진행중에만 설문 참여 가능
		alert("진행중인 설문이 아닙니다.\n진행중인 설문에만 참여할 수 있습니다.");
		return; // 함수 탈출
	}	
	// Radio 'rad_option_text1'의 체크 여부를 검증.
	if(dlt_participateList.getTotalRow() < dlt_QList.getTotalRow()) {
		alert("모든 질문에 대해 응답해 주세요.");
		return; // 함수 탈출
	}

	// 설문 응답 데이터 저장 요청
	com.win.confirm(com.data.getMessage("MSG_CM_00103"), function (res) { // 제출 수정불가 및 제출 확답 알림
		if (res.clickValue === true) {
			com.sbm.execute(sbm_saveSurveyResult);
		}
	});
};

