/**
 * 참여여부 조회 후처리 함수
 */
scwin.sbm_getParticipateOrNotCallback = function(resBody) {
	if (com.sbm.getResultCode(resBody) == gcm.MESSAGE_CODE.STATUS_SUCCESS) {
		var data = resBody.responseJSON.userInfo;
		var resList = resBody.responseJSON.dlt_participateWriteList;
		console.log("resList: ",resList);
		//dlt_participateList.setJSON(resList);

		if(data == "true") {
			tbx_participateTF.setValue('<span style="color:red">이미 참여한 설문입니다.</span>');
			btn_regist.hide(); // 제출 버튼 숨기기
			btn_result.show(); // 결과보기 버튼 보이기
		} else {
			tbx_participateTF.setValue("설문 응답 후 제출 버튼을 눌러 참여해 주세요.");
			btn_regist.show();
			btn_result.hide(); // 결과보기 버튼 숨기기
		}

		// 
		if(resList) { // 참여한 경우
			var gen2Length = generator2.getLength();
			for (var i = 0; i < gen2Length; i++) {
				var gen2Obj_rad = generator2.getChild(i, "rad_option_text1");
				var gen2Obj_reason = generator2.getChild(i, "ipt_choiceReason1");

				var op_rad__cnt = gen2Obj_rad.getItemCount();

				gen2Obj_rad.setSelectedIndex(resList[i].sel_option_num-1); // 선택했던 항목을 선택으로 세팅
				gen2Obj_reason.setValue(resList[i].reason); // 작성했던 선택사유를 작성 세팅

				gen2Obj_rad.setDisabled(true);
				gen2Obj_reason.setDisabled(true);
			}

		}

	}
};


scwin.ipt_choiceReason1_onkeypress = function(e) {
    var inputValue = e.target.value;
	var inputLength = e.target.value.length;
    console.log("입력된 문자열 길이: " + inputLength);

    if (inputLength > 50) {
        alert("50자까지만 적을 수 있어요.");
        e.preventDefault(); // 추가 입력을 막음
    }
};

scwin.ipt_choiceReason1_onkeyup = function(e) {
    var inputValue = e.target.value;
    var inputLength = e.target.value.length;
    console.log("입력된 문자열 길이: " + inputLength);

    if (inputLength > 50) {
        alert("50자까지만 적을 수 있어요.");
        e.target.value = e.target.value.substring(0, 50); // 50자까지만 유지
    }
};

