    q_cnt.setValue(q.length); // 질문 수 업데이트

	for (var i = 0; i < dlt_menu.getRowCount(); i++){
		//surQInfo.setCellData(i, "q_num", i+1); // 문제번호 순번처럼 세팅 (행인덱스,열id,넣을값)
	}
};

